mapper.select:
SELECT p.post_no , u.name AS user_name , (SELECT d.team_name FROM department d WHERE d.team_code = (SELECT u3.team_code FROM user u3 WHERE u3.user_code = p.user_code)) AS team_name , (SELECT ac.code_name FROM all_code ac WHERE ac.code = u.class_code) AS class_name , p.title , p.bHit , p.reg_date , p.fixed_yn FROM post p JOIN board b ON p.board_no = b.board_no JOIN user u ON p.user_code = u.user_code WHERE p.is_del = 0 AND b.board_no = 2
AND p.fixed_yn = 0
AND p.post_team_code = #{param5}
AND (
p.title LIKE CONCAT('%', #{param4}, '%')
EXISTS (SELECT 1 FROM user u2 WHERE u2.user_code = p.user_code AND u2.name LIKE CONCAT('%', #{param4}, '%'))
p.contents LIKE CONCAT('%', #{param4}, '%')
)
ORDER BY p.post_no DESC LIMIT #{param2} OFFSET #{param1}

SELECT p.post_no , u.name AS user_name , (SELECT d.team_name FROM department d WHERE d.team_code = (SELECT u3.team_code FROM user u3 WHERE u3.user_code = p.user_code)) AS team_name , (SELECT ac.code_name FROM all_code ac WHERE ac.code = u.class_code) AS class_name , p.title , p.bHit , p.reg_date , p.fixed_yn FROM post p JOIN board b ON p.board_no = b.board_no JOIN user u ON p.user_code = u.user_code WHERE p.is_del = 0 AND b.board_no = 2 AND p.fixed_yn = 1
AND p.post_team_code = #{param1}
ORDER BY p.post_no DESC LIMIT 5
