mapper.select:
SELECT u.user_code, u.name, d.team_name, cc.code_name AS class_name, pc.code_name AS position_name, u.reg_date FROM `user` u LEFT JOIN department d ON u.team_code = d.team_code LEFT JOIN all_code cc ON u.class_code = cc.code LEFT JOIN all_code pc ON u.position_code = pc.code WHERE pc.code_name IN ('팀장', '팀원') AND u.quit_date is null
AND (
d.team_name LIKE CONCAT('%', #{param4}, '%')
cc.code_name LIKE CONCAT('%', #{param4}, '%')
pc.code_name LIKE CONCAT('%', #{param4}, '%')
u.name LIKE CONCAT('%', #{param4}, '%')
)
AND u.reg_date >= #{startDate}
AND u.reg_date <= #{endDate}
ORDER BY u.reg_date ASC LIMIT #{param1}, #{param2}

SELECT CEIL(COUNT(u.user_code) / #{param1}) FROM `user` u LEFT JOIN department d ON u.team_code = d.team_code LEFT JOIN all_code cc ON u.class_code = cc.code LEFT JOIN all_code pc ON u.position_code = pc.code WHERE u.position_code IN ('P01', 'P02') AND u.quit_date is null
AND (
d.team_name LIKE CONCAT('%', #{param3}, '%')
cc.code_name LIKE CONCAT('%', #{param3}, '%')
pc.code_name LIKE CONCAT('%', #{param3}, '%')
u.name LIKE CONCAT('%', #{param3}, '%')
)
AND u.reg_date >= #{startDate}
AND u.reg_date <= #{endDate}
ORDER BY u.reg_date ASC

mapper.update:
UPDATE user
SET quit_date = CURRENT_DATE, status = '1'
WHERE user_code = #{param} AND quit_date IS NULL;

