mapper.select:
SELECT u.user_code, u.name, tc.code_name AS team_name, cc.code_name AS class_name, pc.code_name AS position_name, u.reg_date FROM `user` u LEFT JOIN all_code tc ON u.team_code = tc.code LEFT JOIN all_code cc ON u.class_code = cc.code LEFT JOIN all_code pc ON u.position_code = pc.code WHERE pc.code_name IN ('팀장', '팀원') AND quit_date is null ORDER BY u.reg_date ASC
AND (
tc.code_name LIKE CONCAT('%', #{param4}, '%')
cc.code_name LIKE CONCAT('%', #{param4}, '%')
pc.code_name LIKE CONCAT('%', #{param4}, '%')
)
AND u.reg_date >= #{startDate}
AND u.reg_date <= #{endDate}
LIMIT #{param1}, #{param2}

SELECT CEIL(COUNT(u.user_code) / #{param1}) FROM `user` u LEFT JOIN all_code tc ON u.team_code = tc.code LEFT JOIN all_code cc ON u.class_code = cc.code LEFT JOIN all_code pc ON u.position_code = pc.code WHERE u.position_code IN ('P01', 'P02') AND quit_date is null ORDER BY u.reg_date ASC
AND (
tc.code_name LIKE CONCAT('%', #{param3}, '%')
cc.code_name LIKE CONCAT('%', #{param3}, '%')
pc.code_name LIKE CONCAT('%', #{param3}, '%')
)
AND u.reg_date >= #{startDate}
AND u.reg_date <= #{endDate}

mapper.update:
UPDATE user
SET quit_date = CURRENT_DATE
WHERE user_code = #{param} AND quit_date IS NULL;
