mapper.select:
SELECT c.course_name , u.name, c.course_start , c.course_end, p.pay_state FROM course c LEFT JOIN `user` u ON c.user_code = u.user_code LEFT JOIN payment p ON c.course_no = p.course_no WHERE p.user_code = '2024U030001'
AND c.course_name LIKE CONCAT('%', #{param3}, '%')
ORDER BY p.pay_code DESC LIMIT #{param1}, #{param2}

SELECT c.course_name AS course_name, u.name AS name, p.course_price, p.pay_date, p.user_code FROM payment p LEFT JOIN course c ON p.course_no = c.course_no LEFT JOIN `user` u ON c.user_code = u.user_code WHERE p.user_code='2024U030001' AND p.pay_date IS NOT NULL
AND c.course_name LIKE CONCAT('%', #{param3}, '%')
LIMIT #{param1}, #{param2}

mapper.update:
UPDATE user
	SET email = #{email}, phone = #{phone}
WHERE user_code = #{user_code}

UPDATE user
	SET photo = #{param2}
WHERE user_code = #{param1}
