mapper.insert:
INSERT INTO schedule
(
	user_code
	, team_code
	, title
	, contents
	, start_date
	, end_date
	, reg_date
	, is_del
	, sked_type
) VALUES(
	#{user_code}
	, #{team_code}
	, #{title}
	, #{contents}
	, #{start_date}
	, #{end_date}
	, NOW()
	, 0
	, #{sked_type}
)

mapper.select:
SELECT sch.sked_no , sch.user_code , sch.title , sch.contents , sch.start_date , sch.end_date , sch.sked_type , usr.name as name , sch.edit_user_code , (select team_name from department where team_code = usr.TEAM_CODE) as team_name FROM schedule sch LEFT JOIN user usr ON usr.user_code = (CASE WHEN sch.edit_user_code IS NOT NULL THEN sch.edit_user_code ELSE sch.user_code END) WHERE 1=1 AND sch.is_del = 0
AND sch.sked_type = #{sked_type}
AND sch.team_code = 'T01'
AND sch.team_code = #{team_code}
AND sch.user_code = #{user_code}
and ( (sch.team_code = #{team_code} and sch.sked_type = 'SKED_TP02') or (sch.user_code = #{user_code} and sch.sked_type = 'SKED_TP03') or (sch.sked_type = 'SKED_TP01') )

mapper.update:
UPDATE schedule 
SET is_del = 1 
WHERE sked_no = #{param1}

UPDATE 
	schedule 
SET edit_user_code = #{user_code}
	, team_code = #{team_code}
	, title = #{title}
	, contents = #{contents}
	, start_date = #{start_date}
	, end_Date = #{end_date}
	, edit_date = NOW()
	, is_del = 0
	, sked_type = #{sked_type}
WHERE 
	sked_no = #{sked_no}

mapper.select:
SELECT user_code 
FROM user 
WHERE team_code = #{param1}
